import math
print("algoritimo de calculo")
n1 = float(input("digite um número"))
raiz = math.sqrt(n1)
pot10 = math.pow(n1, 10)
print("o resultado do número elevado ao quadrado é" , (n1**2))
print("o resultado do número elevado ao cubo é" , (n1**3))
print(f"a raiz quadrada do número é {raiz}")
print(f"o resultado do número elevado a potencia de 10 é de {pot10}" )

